function ajaxLink(){$('a[ajax-poziv="da"]').click(function(){var n=$(this),t,i;console.log(n);t=n.attr("ajax-url");i=n.attr("ajax-rezultat");$.get(t,function(n){$("#"+i).html(n)})});$('button[ajax-poziv="da"]').click(function(){var n=$(this),t,i;console.log(n);t=n.attr("ajax-url");i=n.attr("ajax-rezultat");$.get(t,function(n){$("#"+i).html(n)})})}function ajaxForm(){$('form[ajax-poziv="da"]').submit(function(n){$(this).attr("ajax-poziv","dodan");n.preventDefault();var t=$(this),i=t.attr("ajax-url"),r=t.attr("ajax-rezultat");$.ajax({type:"POST",url:i,data:t.serialize(),success:function(n){$("#"+r).html(n)}})})}function ajaxInit(){var n=$('h2[ajax-poziv="da"]'),t=n.attr("ajax-url"),i=n.attr("ajax-rezultat");$.get(t,function(n){$("#"+i).html(n)})}function notifikacija(){$("#messageBox").delay(3e3).fadeOut(500).slideUp(500,function(){$(this).remove()})}$(document).ready(function(){ajaxInit();notifikacija();ajaxForm()});$(document).ajaxComplete(function(){ajaxLink();ajaxForm()});